mapper.update:
update ord set state = #{state} ,succ = #{succ},uptime = now()

    ,channel= #{channel,jdbcType=INTEGER }


    ,total_time= #{totalTime,jdbcType=INTEGER }

where ordno =#{ordno}

update ord set transfers_state = 2 ,uptime = now()
where transfers_is=1 AND transfers_state=1
    AND biz_no= #{bizNo}

mapper.select:
select SUM(wallet) as wallet from (select wallet,biz_no,CRTIME from  ord   where DATE_SUB(CURDATE(), INTERVAL 29 DAY) <= crtime AND STATE =2 AND pay_way=3 group by crtime)  aa
where biz_no = #{bizNo} and DATE_FORMAT(CRTIME,'%Y-%m-%d')
GROUP BY DATE_FORMAT(CRTIME,'%Y-%m-%d');

select SUM(coins) as coins from (select coins,biz_no,CRTIME from  ord   where DATE_SUB(CURDATE(), INTERVAL 29 DAY) <= crtime AND STATE =2 AND pay_way=3 group by crtime)  aa
where biz_no = #{bizNo} and DATE_FORMAT(CRTIME,'%Y-%m-%d')
GROUP BY DATE_FORMAT(CRTIME,'%Y-%m-%d');

select SUM(prod_price) as prod_price from (select prod_price,biz_no,CRTIME from  ord   where DATE_SUB(CURDATE(), INTERVAL 29 DAY) <= crtime AND STATE =2 AND pay_way=2 group by crtime)  aa
where biz_no = #{bizNo} and DATE_FORMAT(CRTIME,'%Y-%m-%d')
GROUP BY DATE_FORMAT(CRTIME,'%Y-%m-%d');

select sum(prod_price) as prod_price from ord where biz_no = #{bizNo} and pay_way =#{payWay} and TO_DAYS(crtime)= TO_DAYS(now()) and state =2
and dev_type = #{type}
group by biz_no

select sum(prod_price) as prod_price from ord where biz_no = #{bizNo} and pay_way = #{payWay} and TO_DAYS(crtime) >= TO_DAYS(#{mondayOfWeek}) and state =2
and dev_type = #{type}
group by biz_no

SELECT
sum(
case pay_way when 1 then prod_price else 0.00 end
) AS aliInc,
sum(
case pay_way when 2 then prod_price else 0.00 end
) AS wxInc,
sum(
case pay_way when 3 then prod_price else 0.00 end
) AS wltInc
FROM	ord  WHERE biz_no= #{bizNo}
and DATE_FORMAT(crtime, '%Y%m%d') >= #{beginDate}
and DATE_FORMAT(crtime, '%Y%m%d') <= #{endDate}
AND state = 2
and gprs_no=#{gprsNo}
GROUP BY prod_no

SELECT
sum(
case pay_way when 1 then prod_price else 0.00 end
) AS aliInc,
sum(
case pay_way when 2 then prod_price else 0.00 end
) AS wxInc,
sum(
case pay_way when 3 then prod_price else 0.00 end
) AS wltInc
FROM	ord  WHERE  biz_no = #{bizNo}  and
DATE_FORMAT(crtime, '%Y%m%d') >= #{beginDate} and DATE_FORMAT(crtime, '%Y%m%d') <= #{endDate}
and state =2

    and dev_type = #{type}

group by biz_no

SELECT
sum(
case pay_way when 1 then prod_price else 0.00 end
) AS aliInc,
sum(
case pay_way when 2 then prod_price else 0.00 end
) AS wxInc,
sum(
case pay_way when 3 then prod_price else 0.00 end
) AS wltInc
FROM	ord  WHERE  biz_no = #{bizNo}  and state =2

    and dev_type = #{type}

group by biz_no

SELECT
sum(
case pay_way when 1 then prod_price else 0.00 end
) AS aliInc,
sum(
case pay_way when 2 then prod_price else 0.00 end
) AS wxInc,
sum(
case pay_way when 3 then prod_price else 0.00 end
) AS wltInc
FROM	ord  WHERE  biz_no = #{bizNo}  and TO_DAYS(crtime) = TO_DAYS(now()) and state =2

    and dev_type = #{type}

group by biz_no

SELECT
sum(
case pay_way when 1 then prod_price else 0.00 end
) AS aliInc,
sum(
case pay_way when 2 then prod_price else 0.00 end
) AS wxInc,
sum(
case pay_way when 3 then prod_price else 0.00 end
) AS wltInc
FROM	ord  WHERE  biz_no = #{bizNo}  and TO_DAYS(crtime) = (TO_DAYS(now())-1) and state =2

    and dev_type = #{type}

group by biz_no

SELECT
sum(
case pay_way when 1 then prod_price else 0.00 end
) AS aliInc,
sum(
case pay_way when 2 then prod_price else 0.00 end
) AS wxInc,
sum(
case pay_way when 3 then prod_price else 0.00 end
) AS wltInc
FROM	ord  WHERE  biz_no = #{bizNo} and DATE_FORMAT(crtime, '%Y%m') = DATE_FORMAT(now(), '%Y%m') and state =2

    and dev_type = #{type}

group by biz_no

SELECT
sum(
case pay_way when 1 then prod_price else 0.00 end
) AS aliInc,
sum(
case pay_way when 2 then prod_price else 0.00 end
) AS wxInc,
sum(
case pay_way when 3 then prod_price else 0.00 end
) AS wltInc
FROM	ord  WHERE  biz_no = #{bizNo} and TO_DAYS(crtime) >= TO_DAYS(#{mondayOfWeek}) and state =2

    and dev_type = #{type}

group by biz_no

select sum(prod_price) as prod_price from ord where biz_no = #{bizNo} and pay_way = #{payWay} and DATE_FORMAT(crtime, '%Y%m') = DATE_FORMAT(now(), '%Y%m') and state =2
and dev_type = #{type}
group by biz_no

select sum(prod_price) as prod_price from ord where biz_no = #{bizNo} and pay_way =#{payWay} and
DATE_FORMAT(crtime, '%Y%m%d') >= #{beginDate} and DATE_FORMAT(crtime, '%Y%m%d') <= #{endDate}
and state =2

    and dev_type = #{type}

group by biz_no

select sum(prod_price) as prod_price from ord where biz_no = #{bizNo} and pay_way =#{payWay} and state =2

    and dev_type = #{type}

group by biz_no

select * from ord


    order by ${orderField} ${orderSeq}


    order by crtime desc

select * from ord LEFT JOIN ord_pay_wx ON ord.ordno=ord_pay_wx.payno


    order by ${orderField} ${orderSeq}


    order by ord.crtime desc

select
  prod_no as prodNo,  gprs_no as gprsNo,  ordno as ordNo, prod_price as Moeny,  pulse as pulse, succ,  state, crtime, uptime
from ord  WHERE ordno= #{ordNo} and  biz_no= #{bizNo}

select * from ord



    order by ${orderField} ${orderSeq}


    order by ord.crtime desc
